customCSS:
.post.reply, .post.op{
    border: none;
    padding: 15px;
    box-shadow: 3px 6px #0f0f0d;
}
body, is_index.board_g {
    margin-left: 20%;
    margin-right: 20%;
    background-image: url(https://media2.giphy.com/media/UYBDCJjwOd9Re/giphy.gif) !important;
}
.summary{
    z-index: 1 !important;
    font-size: 9px;
}
.fileThumb{
    border-radius: 50px !important;
    z-index: 1;   
}
blockquote {
    display: block;
    -webkit-margin-before: 3px;
    -webkit-margin-after: 3px;
    -webkit-margin-start: 0px;
    -webkit-margin-end: 0px;
}
